// Import necessary dependencies
import React, { useState } from "react";
import { View, Text, TextInput, Pressable, StyleSheet } from "react-native";
import { useNavigation } from "@react-navigation/native";

// Define LoginScreen component
const LoginScreen = () => {
  // Setup useState hooks to handle email and password
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // Use navigation from react navigation
  const navigation = useNavigation();

  const handleGoToRegister = () => {
    // Navigation function to Register screen
    navigation.navigate("Register");
  };

  const handleLogin = async () => {
    try {
      const loginResponse = await fetch("http://localhost:3000/login", {
        method: "POST",
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      if (!loginResponse.ok) {
        console.error('Error during login');
        Alert.alert('Login Error', 'Invalid credentials');
        // Clear fields also in case of failed login
        setEmail(' ');
        setPassword(' ');
        return;
      }
      
      const data = await loginResponse.json();
      await AsyncStorage.setItem('token', data.token);
      
      setEmail(' ');
      setPassword(' ');
      
      navigation.navigate("Dashboard");

    } catch (error) {
      console.error('Error during login:', error);
      Alert.alert('Error', 'Cannot perform login operation');
    }
  };
  
  return (
    <View style={styles.container} accessible={true} accessibilityLabel="YuriShortUrl Login page">
      <Text style={styles.logo}>YuriShortUrl</Text>
      <TextInput
        value={email}
        style={styles.input}
        placeholder="Email"
        onChangeText={(text) => setEmail(text)}
        keyboardType="email-address"
        autoCapitalize="none"
        accessibilityLabel="Email Address"
      />
      <TextInput
        value={password}
        style={styles.input}
        placeholder="Password"
        secureTextEntry={true}
        onChangeText={(text) => setPassword(text)}
        autoCapitalize="none"
        accessibilityLabel="Password"
      />
      <Pressable style={styles.loginButton} onPress={handleLogin} accessibilityLabel="Login Button">
        <Text style={styles.loginButtonText}>Accedi</Text>
      </Pressable>
      <Pressable style={styles.loginButton} onPress={handleGoToRegister} accessibilityLabel="Register Button">
        <Text style={styles.loginButtonText}>Registrati</Text>
      </Pressable>
    </View>
  );
};

// Styles for components
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#fff",
  },
  logo: {
    fontSize: 36,
    color: "#1877F2",
    marginBottom: 20,
  },
  input: {
    width: 300,
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    marginBottom: 10,
    paddingLeft: 10,
  },
  loginButton: {
    backgroundColor: "#1877F2",
    width: 300,
    height: 40,
    justifyContent: "center",
    alignItems: "center",
    borderRadius: 5,
    marginBottom: 10,
  },
  loginButtonText: {
    color: "white",
  },
});

// Export default LoginScreen component
export default LoginScreen;
